mapper.select:

SELECT  COUNT(dl.DL_ID) 
FROM DEAL_LOG dl 
WHERE dl.DL_PROJECT_ID = #{projectId}


SELECT  COUNT(dl.DL_ID) FROM DEAL_LOG dl 
WHERE dl.DL_USER_TYPR = #{userType}
AND dl.DL_USER_ID =${userId}
AND dl.DL_DEAL_STATUS = 0


SELECT  COUNT(dl.DL_ID) FROM DEAL_LOG dl 
WHERE dl.DL_PROJECT_ID = ${projectId}
AND dl.DL_DEAL_STATUS = 0
