mapper.select:
SELECT e.EMPLOYEEID, e.EMPLOYEELOGINNAME, e.EMPLOYEEREALNAME, e.EMPLOYEEDESCRIPTION, e.EMPLOYEEIMG, DATE_FORMAT(e.UPDATEDATE,'%Y-%m-%d %T') AS updateDate, r.ROLEID, r.ROLENAME, r.ROLEDESCRIPTION FROM EMPLOYEE e LEFT JOIN EMPLOYEE_ROLE er ON e.EMPLOYEEID = er.EMPLOYEEID LEFT JOIN ROLE r ON er.ROLEID = r.ROLEID WHERE 1 = 1
AND e.EMPLOYEEREALNAME LIKE CONCAT(CONCAT('%',#{employeeRealName}),'%')
ORDER BY e.EMPLOYEEID DESC LIMIT ${begin} , ${limit}

mapper.update:
UPDATE EMPLOYEE SET EMPLOYEELOGINNAME = #{employeeLoginName}, EMPLOYEEREALNAME = #{employeeRealName},
EMPLOYEEPASSWORD = #{employeePassword},
EMPLOYEEDESCRIPTION = #{employeeDescription}, UPDATEDATE = CURRENT_TIMESTAMP WHERE EMPLOYEEID = ${employeeId}


UPDATE EMPLOYEE
	SET EMPLOYEEIMG = #{employeeImg}
WHERE EMPLOYEEID = ${employeeId}


UPDATE EMPLOYEE
	SET EMPLOYEEPASSWORD = #{employeePassword}
WHERE EMPLOYEEID = ${employeeId}
