mapper.insert:

INSERT INTO FLOW_DATE (
	FD_FLOW_ID,
	FD_STARTTIME,
	FD_TASK_ID
) VALUES(
	#{fdFlowId},
	DATE_FORMAT(#{fdStartTime},'%Y-%m-%d'),
	#{fdTaskId}
);

mapper.update:

UPDATE FLOW_DATE f set 
	f.FD_FLOW_ID=#{fdFlowId},
	f.FD_STARTTIME=DATE_FORMAT(#{fdStartTime},'%Y-%m-%d'),
	f.FD_TASK_ID =#{fdTaskId}
WHERE f.FD_ID = ${fdId};
