mapper.select:
SELECT f.IF_ID AS ifId, f.IF_FLOW_ID AS ifFlowId, f.IF_INDENT_ID AS ifIndentId, f.IF_STATE AS ifState,
fd.FD_ID AS fdId, fd.FD_FLOW_ID AS fdFlowId, DATE_FORMAT(fd.FD_STARTTIME,'%Y-%m-%d') AS fdStartTime, fd.FD_TASK_ID AS fdTaskId
FROM INDENT_FLOW f LEFT JOIN FLOW_DATE fd ON f.IF_FLOW_ID=fd.FD_FLOW_ID HAVING f.IF_INDENT_ID =${id} AND f.IF_STATE ='Y' and fd.FD_TASK_ID = #{key}

mapper.insert:

INSERT INTO INDENT_FLOW (
IF_INDENT_ID,
IF_FLOW_ID,
IF_STATE
) VALUES (
${ifIndentId},
#{ifFlowId},
#{ifState}
);

mapper.update:

UPDATE INDENT_FLOW f SET 
f.IF_INDENT_ID=${ifIndentId},
f.IF_FLOW_ID=#{ifFlowId},
f.IF_STATE=#{ifState}
WHERE f.IF_ID =${ifId}
