mapper.select:

SELECT COUNT(1)
	FROM INDENT t
WHERE t.INDENTTYPE = ${indentType}


SELECT COUNT(1)
	FROM INDENT ind
WHERE ind.SALESMANUNIQUEID = #{salesmanUniqueId}


SELECT SUM(ind.INDENTPRICE)
	FROM INDENT ind
WHERE ind.SALESMANUNIQUEID = #{salesmanUniqueId}

mapper.update:

UPDATE INDENT
	SET INDENTTYPE = ${indentType}
	WHERE 1=1


AND INDENTID IN

	${id}
