,i.IP_USER_TYPE=#{userType}		


,i.IP_USER_ID=${userId}


,i.IP_STATE=${state}


,i.IP_PRICE_FIRST=${priceFirst}


,i.IP_PRICE_LAST=${priceLast}



,i.IP_PRICE_FINISH=${priceFinish}



,i.IP_REFERRER_ID = ${referrerId}



