mapper.select:
SELECT i.IP_ID AS id, i.IP_PROJECT_NAME AS projectName, i.IP_USER_CONTACT AS userContact, i.IP_USER_PHONE AS userPhone, i.IP_TEAM_CONTACT AS teamContact, i.IP_TEAM_PHONE AS teamPhone, i.IP_USER_TYPE AS userType, i.IP_USER_ID AS userId, i.IP_DESCRIPTION AS description, i.IP_SERIAL AS serial, i.IP_SOURCE AS source, i.IP_STATE AS state, i.IP_CUSTOMERID AS customerId, i.IP_TEAMID AS teamId, u.USERNAME AS userName, u.CLIENTLEVEL AS clientLevel, t.TEAMNAME AS teamName, i.IP_PRICE_FIRST AS priceFirst, i.IP_PRICE_LAST AS priceLast, i.IP_PRICE_FINISH AS priceFinish, e.employeeRealName AS referrerName, i.IP_REFERRER_ID AS referrerId, DATE_FORMAT(i.IP_CREATE_TIME,'%Y-%m-%d %T') AS createTime, i.IP_CUSTOMER_PAYMENT AS customerPayment, i.IP_PROVIDER_PAYMENT AS providerPayment
FROM INDENT_PROJECT i LEFT JOIN USERS u on i.ip_customerId=u.id LEFT JOIN TEAM t ON i.ip_teamId = t.teamId LEFT JOIN EMPLOYEE e ON i.ip_referrer_id = e.employeeId WHERE 1=1
AND i.IP_CUSTOMERID = ${customerId}
AND i.IP_TEAMID = ${teamId}
AND 1=2

SELECT i.IP_ID AS id, i.IP_PROJECT_NAME AS projectName, u.USERNAME AS userName, u.CLIENTLEVEL AS clientLevel, i.IP_USER_CONTACT AS userContact, i.IP_USER_PHONE AS userPhone, t.TEAMNAME AS teamName, i.IP_TEAM_CONTACT AS teamContact, i.IP_TEAM_PHONE AS teamPhone, i.IP_USER_TYPE AS userType, i.IP_USER_ID AS userId, i.IP_DESCRIPTION AS description, i.IP_SERIAL AS serial, i.IP_SOURCE AS source, i.IP_STATE AS state, i.IP_CUSTOMERID AS customerId, i.ip_teamId AS teamId, i.IP_PRICE_FIRST AS priceFirst, i.IP_PRICE_LAST AS priceLast, i.IP_PRICE_FINISH AS priceFinish, i.IP_REFERRER_ID AS referrerId, DATE_FORMAT(i.IP_CREATE_TIME,'%Y-%m-%d %T') AS createTime, i.IP_CUSTOMER_PAYMENT AS customerPayment, i.IP_PROVIDER_PAYMENT AS providerPayment
FROM INDENT_PROJECT i LEFT JOIN USERS u ON i.IP_CUSTOMERID=u.id LEFT JOIN TEAM t ON i.IP_TEAMID=t.teamId WHERE 1 = 1
AND i.IP_ID = ${projectId}
AND i.IP_PROJECT_NAME LIKE CONCAT('%',#{projectName},'%')
AND i.IP_STATE = ${state}
AND i.IP_STATE = ${state}
AND i.IP_USER_ID = ${userId}
AND t.TEAMNAME LIKE CONCAT('%',#{teamName},'%')
AND i.ip_teamId = ${teamId}
AND i.IP_SOURCE = #{source}
ORDER BY i.IP_ID DESC LIMIT ${begin} , ${limit}


SELECT COUNT(*)FROM	
(SELECT DISTINCT(i.IP_ID)
FROM INDENT_PROJECT i
LEFT JOIN SYNERGY s
	ON s.s_project_id=i.ip_id 
LEFT JOIN TEAM t
	ON i.IP_TEAMID=t.teamId
LEFT JOIN USERS u 
	ON i.IP_CUSTOMERID=u.id 
LEFT JOIN INDENT_FLOW F 
	ON F.IF_INDENT_ID = i.IP_ID
LEFT JOIN ACT_RU_TASK RES
	ON F.IF_FLOW_ID = RES.PROC_INST_ID_
WHERE 1=1


	AND i.IP_ID = ${projectId}


	AND IP_PROJECT_NAME LIKE CONCAT('%',#{projectName},'%')  


	AND i.IP_STATE = #{state}


	AND i.IP_STATE = ${state}


	AND i.IP_USER_ID = ${userId}


	AND t.TEAMNAME LIKE CONCAT('%',#{teamName},'%') 


	AND u.ID = ${customerId}


	AND i.ip_teamId = ${teamId}


	AND i.IP_SOURCE = #{source}


	AND (s.S_USER_ID = ${userId} OR i.IP_USER_ID = ${userId})


	AND RES.NAME_ LIKE CONCAT('%',#{stage},'%')


)t

