mapper.select:
SELECT id ,title,discription ,content , create_time createTime, update_time updateTime, sort_index sortIndex, `status`, TAGS AS tags, PICLDURL AS picLDUrl, CATEGORY AS category FROM NEWS WHERE 1=1
AND CATEGORY = ${category}
ORDER BY create_time desc LIMIT ${begin} , ${limit}

mapper.update:

	UPDATE NEWS n
      SET n.TITLE = #{title},
      n.CONTENT = #{content},
      n.DISCRIPTION = #{discription},
      n.UPDATE_TIME = CURRENT_TIMESTAMP,
      n.STATUS = ${status},
      n.TAGS = #{tags},
      n.CATEGORY = ${category}



	,n.PICLDURL = #{picLDUrl}



	WHERE n.ID = ${id}
