mapper.select:
SELECT pro.PRODUCTID AS productId, pro.VIDEOURL AS videoUrl, pro.VIDEODESCRIPTION AS videoDescription, pro.PDESCRIPTION AS pDescription, pro.PRODUCTNAME AS productName, pro.PICHDURL AS picHDUrl, pro.PICLDURL AS picLDUrl, pro.PRODUCTTYPE AS productType, pro.FLAG AS flag, i.ITEMNAME AS productTypeName, DATE_FORMAT(pro.UPLOADDATE,'%Y-%m-%d %T') AS uploadDate, pro.RECOMMEND AS recommend, pro.SUPPORTCOUNT AS supportCount, pro.VIDEOLENGTH AS videoLength, pro.TEAMID AS teamId, pro.VISIBLE AS visible, pro.HRET AS hret, t.TEAMNAME AS teamName, pro.TAGS AS tags, pro.SHOWTYPE AS showType, pro.SESSIONID AS sessionId, DATE_FORMAT(pro.CREATIONTIME,'%Y-%m-%d') AS creationTime, pro.MASTERWORK AS masterWork FROM PRODUCT pro LEFT JOIN ITEM i ON i.ITEMID = pro.PRODUCTTYPE LEFT JOIN TEAM t ON t.TEAMID = pro.TEAMID WHERE 1 = 1 AND pro.visible = 0
AND pro.TEAMID = ${teamId}
AND t.TEAMNAME LIKE CONCAT(CONCAT('%',#{teamName}),'%')
AND pro.PRODUCTTYPE = ${productType}
AND pro.PRODUCTNAME LIKE CONCAT(CONCAT('%',#{productName}),'%')
AND DATE_FORMAT(pro.UPLOADDATE,'%Y-%m-%d %T') = #{uploadDate}
AND pro.RECOMMEND = ${recommend}
AND pro.SUPPORTCOUNT = ${supportCount}
AND pro.VIDEOLENGTH = #{videoLength}
AND pro.FLAG = #{flag}
AND pro.HRET <> ''
AND pro.HRET = ''
ORDER BY pro.recommend,pro.supportCount desc LIMIT ${begin} , ${limit}

mapper.update:
UPDATE PRODUCT SET VIDEODESCRIPTION = #{videoDescription}, PDESCRIPTION = #{pDescription}, PRODUCTNAME = #{productName}, PRODUCTTYPE = ${productType}, VIDEOLENGTH = #{videoLength}, TEAMID = ${teamId}, FLAG = ${flag}, VISIBLE = ${visible}, HRET = #{hret}, TAGS = #{tags}, UPDATEDATE = CURRENT_TIMESTAMP, SHOWTYPE = ${showType}, SESSIONID = #{sessionId}, RECOMMEND = ${recommend}, SUPPORTCOUNT = ${supportCount}
,VIDEOURL = #{videoUrl}
,PICHDURL = #{picHDUrl}
,PICLDURL = #{picLDUrl}
,CREATIONTIME = DATE_FORMAT(#{creationTime},'%Y-%m-%d')
WHERE PRODUCTID = ${productId}


UPDATE PRODUCT
	SET VIDEOURL = #{videoUrl},
	PICHDURL = #{picHDUrl},
	PICLDURL = #{picLDUrl}
WHERE PRODUCTID = ${productId}

mapper.select:
SELECT pro.PRODUCTID AS productId, pro.VIDEOURL AS videoUrl, pro.VIDEODESCRIPTION AS videoDescription, pro.PDESCRIPTION AS pDescription, pro.PRODUCTNAME AS PRODUCTNAME, pro.PICHDURL AS PICHDURL, pro.PICLDURL AS picLDUrl, pro.PRODUCTTYPE AS productType, pro.VISIBLE AS visible, DATE_FORMAT(pro.UPLOADDATE,'%Y-%m-%d %T') AS uploadDate, pro.RECOMMEND AS recommend, pro.SUPPORTCOUNT AS supportCount, pro.VIDEOLENGTH AS videoLength, pro.HRET AS hret, pro.TAGS AS tags, pro.SHOWTYPE AS showType, pro.SESSIONID AS sessionId, DATE_FORMAT(pro.CREATIONTIME,'%Y-%m-%d') AS creationTime, pro.MASTERWORK AS masterWork FROM PRODUCT pro WHERE 1 = 1
AND pro.PRODUCTID IN
${id}


SELECT COUNT(1)
	FROM PRODUCT pro
	LEFT JOIN TEAM t ON t.TEAMID = pro.TEAMID
WHERE 1 = 1
AND pro.visible = 0


AND pro.TEAMID = ${teamId}


AND t.TEAMNAME  LIKE CONCAT(CONCAT('%',#{teamName}),'%')


AND pro.PRODUCTTYPE = ${productType}


AND DATE_FORMAT(pro.UPLOADDATE,'%Y-%m-%d %T') =
#{uploadDate}



AND pro.VIDEOLENGTH = #{videoLength}


AND pro.FLAG = ${flag}



	AND pro.HRET <> ''



AND pro.HRET = ''

SELECT pro.PRODUCTID AS productId, pro.VIDEOURL AS videoUrl, pro.VIDEODESCRIPTION AS videoDescription, pro.PDESCRIPTION AS pDescription, pro.PRODUCTNAME AS productName, pro.PICHDURL AS picHDUrl, pro.PICLDURL AS picLDUrl, pro.PRODUCTTYPE AS productType, pro.HRET AS hret, i.ITEMNAME AS productTypeName, DATE_FORMAT(pro.UPLOADDATE,'%Y-%m-%d %T') AS uploadDate, pro.RECOMMEND AS recommend, pro.SUPPORTCOUNT AS supportCount, pro.VIDEOLENGTH AS videoLength, pro.VISIBLE AS visible, pro.TEAMID AS teamId, t.TEAMNAME AS teamName, pro.TAGS AS tags, pro.SHOWTYPE AS showType, pro.SESSIONID AS sessionId, DATE_FORMAT(pro.CREATIONTIME,'%Y-%m-%d') AS creationTime, pro.MASTERWORK AS masterWork FROM PRODUCT pro LEFT JOIN ( SELECT MIN(serviceRealPrice) AS SERVICEREALPRICE,MIN(servicePrice) AS SERVICEPRICE,productId FROM SERVICE GROUP BY productId HAVING productId ) ser ON pro.PRODUCTID = ser.PRODUCTID LEFT JOIN ITEM i ON i.ITEMID = pro.PRODUCTTYPE LEFT JOIN TEAM t ON t.TEAMID = pro.TEAMID WHERE 1 = 1 AND pro.FLAG = 1 AND pro.VISIBLE = 0 AND t.FLAG = 1
AND pro.PRODUCTTYPE = ${productType} AND i.ISACTIVE = 0
AND pro.VIDEOLENGTH BETWEEN ${lengthBegin} AND ${lengthEnd}
AND ser.SERVICEREALPRICE BETWEEN ${priceBegin} AND ${priceEnd}
LIMIT ${begin} , ${limit}

SELECT COUNT(1) FROM PRODUCT pro LEFT JOIN ( SELECT MIN(serviceRealPrice) AS SERVICEREALPRICE,MIN(servicePrice) AS SERVICEPRICE,productId FROM SERVICE GROUP BY productId HAVING productId ) ser ON pro.PRODUCTID = ser.PRODUCTID LEFT JOIN TEAM t ON pro.TEAMID = t.TEAMID LEFT JOIN ITEM i ON pro.PRODUCTTYPE = i.ITEMID WHERE pro.FLAG = 1 AND pro.visible = 0 AND t.FLAG = 1
AND pro.PRODUCTTYPE = ${productType} AND i.ISACTIVE = 0
AND pro.VIDEOLENGTH BETWEEN ${lengthBegin} AND ${lengthEnd}
AND ser.SERVICEREALPRICE BETWEEN ${priceBegin} AND ${priceEnd}

mapper.update:

UPDATE PRODUCT
	SET PDESCRIPTION = #{pDescription},
	PRODUCTNAME = #{productName},
	PRODUCTTYPE = ${productType},
	VIDEOLENGTH = #{videoLength},
	VISIBLE = ${visible},
	TAGS = #{tags},
	VIDEODESCRIPTION = #{videoDescription},
	UPDATEDATE = CURRENT_TIMESTAMP,
	SESSIONID = #{sessionId}


,VIDEOURL = #{videoUrl} 


,PICHDURL = #{picHDUrl} 


,PICLDURL = #{picLDUrl} 


,CREATIONTIME = DATE_FORMAT(#{creationTime},'%Y-%m-%d')


WHERE PRODUCTID = ${productId}


UPDATE PRODUCT p SET
	p.VIDEODESCRIPTION = #{videoDescription}
WHERE
	p.PRODUCTID = ${productId}

mapper.select:


SELECT * FROM PRODUCT 
WHERE teamId = ${teamId}
AND masterWork = 1
