mapper.select:
SELECT sale.SALESMANID AS salesmanId, sale.SALESMANNAME AS salesmanName, sale.SALESMANDESCRIPTION AS salesmanDescription, sale.UNIQUEID AS uniqueId, DATE_FORMAT(sale.CREATEDATE,'%Y-%m-%d %T') AS createDate, DATE_FORMAT(sale.UPDATEDATE,'%Y-%m-%d %T') AS updateDate FROM SALESMAN sale WHERE 1 = 1
AND sale.UNIQUEID = #{uniqueId}
AND sale.SALESMANNAME LIKE CONCAT(CONCAT('%',#{salesmanName}),'%')
ORDER BY sale.SALESMANID DESC LIMIT ${begin} , ${limit}

SELECT COUNT(1) FROM SALESMAN sale WHERE 1 = 1
AND sale.UNIQUEID = #{uniqueId}
AND sale.SALESMANNAME LIKE CONCAT(CONCAT('%',#{salesmanName}),'%')

mapper.insert:

INSERT INTO SALESMAN(SALESMANNAME,
					   SALESMANDESCRIPTION,
					   UNIQUEID
)
VALUES (
	#{salesmanName},
	#{salesmanDescription},
	#{uniqueId}
)
