mapper.select:
SELECT t.TEAMID AS teamId, t.TEAMNAME AS teamName, t.TEAMPHOTOURL AS teamPhotoUrl, t.TEAMDESCRIPTION AS teamDescription, t.RECOMMENDATION AS recommendation, t.CITY AS city, t.LINKMAN AS linkman, t.WEBCHAT AS webchat, t.QQ AS qq, t.OFFICIALSITE AS officialSite, t.SCALE AS scale, t.ESTABLISHDATE AS establishDate, t.BUSINESS AS business, t.BUSINESSDESC AS businessDesc, t.PRICERANGE AS priceRange, t.DEMAND AS demand, t.INFORESOURCE AS infoResource, t.DESCRIPTION AS description, DATE_FORMAT(t.CREATEDATE,'%Y-%m-%d %T') AS createDate, DATE_FORMAT(t.UPDATEDATE,'%Y-%m-%d %T') AS updateDate, t.PHONENUMBER AS phoneNumber, t.EMAIL AS email, t.ADDRESS AS address, t.FLAG AS flag, t.LOGINNAME AS loginName, p.PROVINCE AS teamProvinceName, c.CITY AS teamCityName, t.TEAMPROVINCE AS teamProvince, t.TEAMCITY AS teamCity FROM TEAM t LEFT JOIN PROVINCE p ON t.teamProvince = p.provinceID LEFT JOIN CITY c on t.teamCity = c.cityID WHERE 1 = 1 AND t.flag <> 3
AND t.TEAMID = ${teamId}
AND t.FLAG = ${flag}
AND t.PRICERANGE = #{priceRange}
AND t.TEAMNAME LIKE CONCAT('%',#{teamName},'%')
AND t.PHONENUMBER LIKE CONCAT('%',#{phoneNumber},'%')
t.BUSINESS LIKE CONCAT(CONCAT('%',#{bus}),'%')
ORDER BY
${sort} ${order},
t.OD ASC,UPDATEDATE DESC,CREATEDATE DESC LIMIT ${begin} , ${limit}


SELECT COUNT(1)
FROM TEAM t
	WHERE 1 = 1
		AND t.flag <> 3


AND t.TEAMID = ${teamId}


AND t.TEAMNAME LIKE CONCAT('%',#{teamName},'%')


AND t.FLAG = #{flag}



AND t.LOGINNAME = #{loginName}



AND t.PRICERANGE = #{priceRange}


AND t.PHONENUMBER LIKE CONCAT('%',#{phoneNumber},'%')



	t.BUSINESS LIKE CONCAT(CONCAT('%',#{bus}),'%')

mapper.update:
UPDATE TEAM SET TEAMNAME = #{teamName}, TEAMDESCRIPTION = #{teamDescription}, EMAIL = #{email}, ADDRESS = #{address}, FLAG = ${flag}, RECOMMENDATION = #{recommendation}, CITY = ${city}, LINKMAN = #{linkman}, WEBCHAT = #{webchat}, QQ = #{qq}, OFFICIALSITE = #{officialSite}, SCALE = #{scale}, ESTABLISHDATE = #{establishDate}, BUSINESS = #{business}, BUSINESSDESC = #{businessDesc}, PRICERANGE = ${priceRange}, DEMAND = #{demand}, INFORESOURCE = ${infoResource}, DESCRIPTION = #{description}, PHONENUMBER = #{phoneNumber}
,OD = ${od}
,LOGINNAME = #{loginName}
,PHONENUMBER = #{phoneNumber}
,TEAMPROVINCE = #{teamProvince}
,TEAMCITY = #{teamCity}
WHERE TEAMID = ${teamId}


UPDATE TEAM
	SET TEAMPHOTOURL = #{teamPhotoUrl}
WHERE TEAMID = ${teamId}

mapper.select:
SELECT t.TEAMID AS teamId, t.TEAMPHOTOURL AS teamPhotoUrl FROM TEAM t WHERE 1 = 1 AND t.flag <> 3
AND TEAMID IN
${id}

mapper.update:

UPDATE TEAM
	SET PASSWORD = #{password}
WHERE PHONENUMBER = #{phoneNumber}

UPDATE TEAM SET TEAMNAME = #{teamName}, TEAMDESCRIPTION = #{teamDescription}, EMAIL = #{email}, ADDRESS = #{address}, UPDATEDATE = CURRENT_TIMESTAMP, CITY = ${city}, LINKMAN = #{linkman}, WEBCHAT = #{webchat}, QQ = #{qq}, OFFICIALSITE = #{officialSite}, SCALE = #{scale}, ESTABLISHDATE = #{establishDate}, BUSINESS = #{business}, BUSINESSDESC = #{businessDesc}, PRICERANGE = ${priceRange}, DEMAND = #{demand}, INFORESOURCE = ${infoResource}, DESCRIPTION = #{description}, TEAMPROVINCE = #{teamProvince}, teamCity = #{teamCity}
,PHONENUMBER = #{phoneNumber}
WHERE TEAMID = ${teamId}


UPDATE TEAM
	SET PASSWORD = #{password}
WHERE LOGINNAME = #{loginName}


UPDATE TEAM
	SET FLAG = 0
WHERE TEAMID = ${teamId}

