mapper.select:
SELECT i.INVOICE_ID AS invoiceId, i.INVOICE_CODE AS invoiceCode, i.INVOICE_TYPE AS invoiceType, i.INVOICE_CONTENT AS invoiceContent, FORMAT(i.INVOICE_TOTAL,2) AS invoiceTotal, i.INVOICE_RADIO AS invoiceRadio, DATE_FORMAT(i.INVOICE_STAMPTIME,'%Y-%m-%d')AS invoiceStampTime, DATE_FORMAT( i.INVOICE_RECEIVETIME,'%Y-%m-%d') AS invoiceReceiveTime, i.INVOICE_USERID AS invoiceUserId, i.INVOICE_PROJECTID AS invoiceProjectId, i.INVOICE_EMPLOYEEID AS invoiceEmployeeId, i.INVOICE_NOTICE AS invoiceNotice, i.INVOICE_STATUS AS invoiceStatus, i.REASON AS reason, p.IP_PROJECT_NAME AS projectName, u.USERNAME AS userName, e.employeeRealName AS invoiceEmployeeName FROM USER_INVOICE i INNER JOIN INDENT_PROJECT p ON i.INVOICE_PROJECTID = p.IP_ID LEFT JOIN USERS u ON i.INVOICE_USERID = u.ID LEFT JOIN EMPLOYEE e ON i.invoice_employeeId = e.employeeId WHERE 1 = 1
AND i.INVOICE_TYPE = ${invoiceType}
AND i.INVOICE_CODE LIKE CONCAT('%',#{invoiceCode},'%')
AND i.INVOICE_PROJECTID = ${invoiceProjectId}
ORDER BY
${sort} ${order},
i.INVOICE_RECEIVETIME DESC LIMIT ${begin} , ${limit}

SELECT COUNT(1) FROM USER_INVOICE i INNER JOIN INDENT_PROJECT p ON i.INVOICE_PROJECTID = p.IP_ID LEFT JOIN USERS u ON i.INVOICE_USERID = u.ID LEFT JOIN EMPLOYEE e ON i.invoice_employeeId = e.employeeId WHERE 1 = 1
AND i.INVOICE_TYPE = ${invoiceType}
AND i.INVOICE_CODE LIKE CONCAT('%',#{invoiceCode},'%')
AND i.invoice_projectId = ${invoiceProjectId}

mapper.delete:
DELETE FROM USER_INVOICE WHERE INVOICE_ID = -1
OR INVOICE_ID IN
${id}

mapper.update:
UPDATE USER_INVOICE SET INVOICE_STATUS = 1, REASON = '' WHERE INVOICE_ID = -1
OR INVOICE_ID IN
${id}


UPDATE USER_INVOICE
	SET INVOICE_STATUS = 2,
		REASON = #{reason}
	WHERE INVOICE_ID = ${invoiceId}
