mapper.select:
SELECT u.ID AS id, u.USERNAME AS userName, u.PASSWORD AS password, u.TELEPHONE AS telephone, u.EMAIL AS email, u.IMGURL AS imgUrl, u.SEX AS sex, u.QQ AS qq, u.REALNAME as realName, u.USERCOMPANY AS userCompany, u.CLIENTLEVEL AS clientLevel, DATE_FORMAT(u.BIRTHDAY,'%Y-%m-%d') AS birthday, DATE_FORMAT(u.CREATEDATE,'%Y-%m-%d %T') AS createDate, NOTE AS note FROM USERS u WHERE 1 = 1
AND u.USERNAME LIKE CONCAT(CONCAT('%',#{userName}),'%')
AND u.clientLevel IS NULL
AND u.clientLevel = ${clientLevel}
ORDER BY CREATEDATE,ID DESC LIMIT ${begin} , ${limit}

mapper.insert:
INSERT INTO USERS( USERNAME, TELEPHONE, PASSWORD, EMAIL, BIRTHDAY, IMGURL, SEX, REALNAME, QQ, USERCOMPANY, NOTE
,CLIENTLEVEL
) VALUES( #{userName}, #{telephone}, #{password}, #{email}, DATE_FORMAT(#{birthday},'%Y-%m-%d'), #{imgUrl}, ${sex}, #{realName}, #{qq}, #{userCompany}, #{note}
,${clientLevel}
)


INSERT INTO USERS(
	USERNAME,
	TELEPHONE,
	REALNAME
) VALUES(
	#{userName},
	#{telephone},
	#{realName}
)

mapper.select:

SELECT COUNT(1)
	FROM USERS u
WHERE u.TELEPHONE = #{telephone}

mapper.update:

UPDATE USERS
	SET PASSWORD = #{password}
WHERE 1 = 1


AND TELEPHONE = #{userName}
OR  EMAIL = #{userName}


UPDATE USERS
	SET PASSWORD = #{password}
WHERE id = ${id}


UPDATE USERS
	SET TELEPHONE = #{telephone}
WHERE id = ${id}


UPDATE USERS
	SET IMGURL = #{imgUrl}
WHERE id = ${id}
